import React, { useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { getItems } from '../reducks/items/selector';
import { FetchItems } from '../reducks/items/operations';
import Header from '../components/Common/Header';
import Footer from '../components/Common/Footer';
import brush from '../assets/img/Brush_1_.png';
import {Link} from 'react-router-dom'
import Item from '../components/Common/Item';
import { getCarts, getSubtotal } from '../reducks/carts/selectors';

const Home = () => {
    const selector = useSelector((state) => state);
    const [showCart,setShowCart] = useState(false)
    const items = getItems(selector)
    const carts = getCarts(selector)
    const subtotal = getSubtotal(selector)
    console.log(items)
    const dispatch = useDispatch();


    useEffect(() => {
        dispatch
        dispatch(FetchItems())
    }, [dispatch])

 
    const showItem = (item) => {
        return (
            <div>
                <Item
                key={item.id}
                item={item} />
            </div>
        )
    }

    return (
        <>
        <Header/>
        <section class="all-mid">
        
        <div class="start">
            <h3 class="start-title">Our Most Popular Recipes</h3>
        <img class="brush" src={brush} alt="" />
        <p>Try our Most Delicious food and it usually takes minutes to deliver!</p>
        </div>
        
        <div class="btns">
            <button>All</button>
            <Link to='/?category=hot'>HOT</Link>
            <button>COLD</button>
            <button id="bagel">BAGEL</button>
        </div>
        <div className='menu'>
            {items && items.map((item) => showItem(item))}
        </div>
        </section>
        <Footer/>
        </>
    )
}

export default Home
