import React from 'react'
import { useDispatch } from 'react-redux'
import { addCart, decreaseCart, increaseCart } from '../../reducks/carts/operations';

const Item = ({item,selected_count,setShowReviews,setSelectedItemId}) => {
    const dispatch = useDispatch();

    const clickAddCart = () => {
        dispatch(addCart(item))
    }

    const clickPlusCart = () => {
        dispatch(increaseCart(item))
    }

    const clickMinusCart = () => {
        dispatch(decreaseCart(item))
    }
    const checkReviews = ( ) => {
        setSelectedItemId(item.id)
        setShowReviews (true)
    }


  return (
        <div className="items" key={item.id}>
                    <img src={item.image} alt="pic" />
                    <div className="numReviews">
                        <p>44</p>
                    </div>
                    <p className="item-name">Chicken Submarine Burger</p>
                    <div className="review">
                        <a href="">Write Review</a>
                        <p href="" onClick={() => checkReviews(true)}>Check Reviews</p>
                    </div>
                    <div id="cart">
                        {selected_count == 0 ? (
                            <div className='add' onClick={clickAddCart}>
                                <button>Add To Cart</button>
                            </div>
                        ):(
                            <div className='number'>
                                <span className='minus' onClick={clickMinusCart}> - </span>
                                <span className='count'>{selected_count}</span>
                                <span className='plus' onClick={clickPlusCart}> + </span>
                            </div>
                        )}
                        <div className='price'>${item.price}</div>
                    </div>
                </div>
  )
}

export default Item
